DROP TABLE IF EXISTS AERO;
CREATE TABLE AERO(
   Status           VARCHAR(19) NOT NULL 
  ,Restrictions     VARCHAR(3) NOT NULL
  ,Prerequistes     VARCHAR(3) NOT NULL
  ,Title            VARCHAR(30) NOT NULL
  ,Type             VARCHAR(10) NOT NULL
  ,Section          VARCHAR(2) NOT NULL
  ,Course_Number    DATE NOT NULL
  ,Also_Register_In VARCHAR(62)
  ,Credits          NUMERIC(2,1)
  ,Instructor_Name  VARCHAR(17)
  ,Semester         TEXT
  ,Meeting_Date     TEXT
  ,CRN              INTEGER NOT NULL PRIMARY KEY
);

